INSERT INTO `cmsclientdetailslanguagestext` (`ClientDetailsLanguageTextID`, `CMSClientDetailID`, `LanguageCode`, `CMSClientPersonalDescriptionText`, `CMSClientPoliticalDescriptionText`, `CMSClientSocialDescriptionText`, `visionmissionText`) VALUES
(75, 13, 'MAR', 'नरहरी झीरवळ यांनी नाशिकच्या मराठा विद्या प्रसारक मंडळाचे के.टी.एच.एम कॉलेज येथून बी.ए (प्रथम वर्ष) पर्यंत शिक्षण घेतले आहे. ते विवाहित असून त्यांचा परंपरागत शेती हा व्यवसाय आहे. २०१४ सालच्या निवडणुकीत ते १२००० पेक्षा अधिक मतांनी निवडून आले.&nbsp;ते दिंडोरी (नाशिक) येथले आमदार असून तळागाळातल्या जनतेचे प्रतिनिधी म्हणून अल्पावधीतच प्रसिद्ध झालेत. ते वयाच्या ५५ व्या वर्षीही एका तरुणाला लाजवेल इतक्या झपाट्याने काम करता आहेत.&nbsp;', '<p class=\"MsoListParagraphCxSpFirst\" style=\"text-indent:-.25in;mso-list:l0 level1 lfo1\"><font face=\"Symbol\">•<span class=\"Apple-tab-span\" style=\"white-space:pre\">	</span>विविध संघटनांच्या १९८७ पासून अध्यक्ष &nbsp;</font></p><p class=\"MsoListParagraphCxSpFirst\" style=\"text-indent:-.25in;mso-list:l0 level1 lfo1\"><font face=\"Symbol\">•<span class=\"Apple-tab-span\" style=\"white-space:pre\">	</span>१९८७ पासून सदस्य - १५ वर्षे सरपंच (ग्राम्पांच्यात, वानरे)</font></p><p class=\"MsoListParagraphCxSpFirst\" style=\"text-indent:-.25in;mso-list:l0 level1 lfo1\"><font face=\"Symbol\">•<span class=\"Apple-tab-span\" style=\"white-space:pre\">	</span>१९९२ - जनता पक्षात असताना सावतामाळी दूध सहकारी संघाचा संस्थापक आणि अध्यक्ष</font></p><p class=\"MsoListParagraphCxSpFirst\" style=\"text-indent:-.25in;mso-list:l0 level1 lfo1\"><font face=\"Symbol\">•<span class=\"Apple-tab-span\" style=\"white-space:pre\">	</span>१९९४ – काँग्रेस पक्ष सदस्य</font></p><p class=\"MsoListParagraphCxSpFirst\" style=\"text-indent:-.25in;mso-list:l0 level1 lfo1\"><font face=\"Symbol\">•<span class=\"Apple-tab-span\" style=\"white-space:pre\">	</span>१९९९ – &nbsp;राष्ट्रवादी काँग्रेसचे सदस्य (राष्ट्रवादी मुख्य, दिंडोरी तालुका)</font></p><p class=\"MsoListParagraphCxSpFirst\" style=\"text-indent:-.25in;mso-list:l0 level1 lfo1\"><font face=\"Symbol\">•<span class=\"Apple-tab-span\" style=\"white-space:pre\">	</span>१० वर्षे सदस्य आणि उपसभापती (ग्राम पंचायत समिती, दिंडोरी)</font></p><p class=\"MsoListParagraphCxSpFirst\" style=\"text-indent:-.25in;mso-list:l0 level1 lfo1\"><font face=\"Symbol\">•<span class=\"Apple-tab-span\" style=\"white-space:pre\">	</span>जिल्हा परिषद, नाशिक २००१ सदस्य पासून</font></p><p class=\"MsoListParagraphCxSpFirst\" style=\"text-indent:-.25in;mso-list:l0 level1 lfo1\"><font face=\"Symbol\">•<span class=\"Apple-tab-span\" style=\"white-space:pre\">	</span>ऑक्टोबर २००४ पासून, आमदार महाराष्ट्र विधानसभा</font></p>', 'मराठा विद्या प्रसारक समाज कॉलेज (कला, वाणिज्य, विज्ञान), नाशिक मधल्या व्यवस्थापन समिती मध्ये कार्यरत. &nbsp;<div><br></div><div><div>दिंडोरी येथील अरुणोदय आणि ईशान्येश्वर लायब्ररी आणि इतर शैक्षणिक संस्थासाठी १ कोटी १६ लाख च्या देणग्या दिल्या</div><div><br></div><div>· &nbsp; &nbsp; &nbsp; &nbsp; अनेक शैक्षणिक संस्थांना सुविधा प्रदान केल्या आणि देणग्या दिल्या</div><div><br></div><div>· &nbsp; &nbsp; &nbsp; &nbsp; आदिवासींच्या चुकीच्या प्रथा निर्मूलनासाठी प्रचंड प्रयत्न केले</div><div><br></div><div>· &nbsp; &nbsp; &nbsp; &nbsp; अनेक थिएटर, खेळ आणि सांस्कृतिक कार्यक्रम आयोजित केले</div></div>', NULL),
